import React, {useState} from 'react';
import Expense from "./component/ExpenseItem";
import Card from "./component/Card";
import NewExpense from "./NewExpense/NewExpense";
import ExpenseFilter from "./NewExpense/ExpenseFilter";

function App() {
  const expenses = [
    {
      title: "Car Insurance",
      amount: "$299.99",
      date: new Date(2021, 3, 28),
    },
    {
      title: "Health Insurance",
      amount: "$459",
      date: new Date(2021, 2, 12),
    },
    {
      title: "Books",
      amount: "$123",
      date: new Date(2021, 4, 21),
    },
    {
      title: "College Fee",
      amount: "$4000",
      date: new Date(2021, 4, 4),
    },
  ];

  const addNewHandler = (expenseData) => {
    const expenseInfo = {
      ...expenseData,
    };
  };
  const [filteredYear, setFilteredYear] = useState('2020');
  const [filterInfoText, setFilterInfoText]=useState('2019, 2021 & 2022');
  const changeSelectHandler = (year) => {
     setFilteredYear(year);
     if(year==='2019'){
      setFilterInfoText('2020, 2021 & 2022');
     }
     else if(year==='2020'){
      setFilterInfoText('2019, 2021 & 2022');
     }
     else if(year==='2021'){
      setFilterInfoText('2019, 2020 & 2022');
     }
     else{
      setFilterInfoText('2020, 2020 & 2021');
     }
  };
  return (
    <div>
      <NewExpense onAddingNew={addNewHandler} />
      <Card>
        <ExpenseFilter selected={filteredYear} onChangeSelect={changeSelectHandler} />
        <p>Data for years {filterInfoText} is hidden.</p>
        <Expense
          title={expenses[0].title}
          amount={expenses[0].amount}
          date={expenses[0].date}
        />
        <Expense
          title={expenses[1].title}
          amount={expenses[1].amount}
          date={expenses[1].date}
        />
        <Expense
          title={expenses[2].title}
          amount={expenses[2].amount}
          date={expenses[2].date}
        />
        <Expense
          title={expenses[3].title}
          amount={expenses[3].amount}
          date={expenses[3].date}
        />
      </Card>
    </div>
  );
}

export default App;
