import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
    const [expenseTitle, setTitle]=useState('');
    const [expenseAmount, setAmount]=useState('');
    const [expenseDate, setDate]=useState('');

  const ExpenseTitleHandler = (event) => {
    setTitle(event.target.value);
  };
  const ExpenseAmountHandler = (event) => {
    setAmount(event.target.value);
  };
  const ExpenseDateHandler = (event) => {
    setDate(event.target.value);
  };
  return (
    <form>
      <div className="new-expense-controls tit">
        <div className="new-expense-control">
          <label>Title:</label>
          <input type="text" onChange={ExpenseTitleHandler} />
        </div>
        <div className="new-expense-control amt">
          <label>Amount:</label>
          <input type="number" min="0.01" step="0.01" onChange={Ex}/>
        </div>
        <div className="new-expense-control cal">
          <label>Calendar:</label>
          <input
            className="date"
            onChange={ExpenseDateHandler}
            type="date"
            min="2019-01-01"
            max="2023-12-31"
          />
        </div>
      </div>
      <div className="new-expense_actions btn">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
