import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setTitle]=useState('');
  const [enteredAmount, setAmount]=useState('');
  const [enteredDate, setDate]=useState('');

//   const [userInput, setUserInput] = useState({
//     enteredTitle: "",
//     enteredAmount: "",
//     enteredDate: "",
//   });

  const ExpenseTitleHandler = (event) => {
    setUserInput()
        enteredTitle: event.target.value;
  };
  const ExpenseAmountHandler = (event) => {
    setUserInput((prevState) => {
      return {
        ...prevState,
        enteredAmount: event.target.value,
      };
    });
  };
  const ExpenseDateHandler = (event) => {
    setUserInput((prevState) => {
      return {
        ...prevState,
        enteredDate: event.target.value,
      };
    });
  };
  const submitHandler = (event) => {
    event.preventDefault();
    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
  };
  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense-controls tit">
        <div className="new-expense-control">
          <label>Title:</label>
          <br />
          <input type="text" onChange={ExpenseTitleHandler} />
        </div>
        <div className="new-expense-control amt">
          <label>Amount:</label>
          <br />
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={ExpenseAmountHandler}
          />
        </div>
        <div className="new-expense-control cal">
          <label>Date:</label>
          <br />
          <input
            className="date"
            onChange={ExpenseDateHandler}
            type="date"
            min="2019-01-01"
            max="2023-12-31"
          />
        </div>
      </div>
      <div className="new-expense_actions btn">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
